// YEAR
$(function () {
    var thisYear = new Date().getFullYear();
    var releaseYear = parseInt($('#release_year').text());

    if (releaseYear === thisYear) {
        $('#this_year').hide();
    } else {
        $('#this_year').text(' - ' + thisYear);
    }
});

