// ACTIVE MENU
$('.navbar nav>ul>li>a,.navbar nav .dropdown ul li a,.course-chapter .list-group-item').each(function () {
    var currentHref = $(this).prop('href');

    if (currentHref === window.location.href) {
        $(this).addClass('active');
        $(this).parents('li').addClass('active');
    }
});

// RATING INFO
$('.course-rating').each(function () {
    var rating = parseFloat($(this).find('.rating b').text());
    var starIcon = Math.round(rating * 2) / 2;

    for (var i = 5; i >= 1; i--) {
        var star;

        if (i <= starIcon) {
            star = $('<span class="material-symbols fill">star</span>');
        } else if (i - 0.5 === starIcon) {
            star = $('<span class="material-symbols">star_half</span>');
        } else {
            star = $('<span class="material-symbols">star_border</span>');
        }

        $(this).find('.rating').prepend(star);
    }
});